import React, { useState } from "react";
import logo from "../img/agency-hab-logo-50.png";

function GeneratedSignature({
  fullName,
  jobTitle,
  emailAddress,
  address,
  color, // Add this prop
}) {
  const [phoneNumber, setPhoneNumber] = useState("");

  const handlePhoneNumberChange = (event) => {
    // Remove non-numeric characters including dashes, periods, and parentheses
    const cleanedPhoneNumber = event.target.value.replace(/[^0-9]/g, "");
    setPhoneNumber(cleanedPhoneNumber);
  };
  return (
    <div
      className="tableContainer padding-lg radius-lg"
      style={{
        background: "white",
        width: "fit-content",
      }}
    >
      <table
        className="generatedSignature"
        cellSpacing="0"
        cellPadding="0"
        border="0"
        width="500"
        style={{
          borderCollapse: "separate",
          borderSpacing: "10px",
        }}
      >
        <tr>
          {/* Top Left Quadrant: Logo */}
          <td width="200" height="150">
            <a
              href="https://cutera.com"
              style={{
                height: "100%",
                width: "100%",
                display: "block",
              }}
            >
              <img
                src="https://placekitten.com/g/200/300"
                alt="Cutera"
                width="70%"
                className="radius-lg"
                style={{
                  objectFit: "cover",
                  height: "100%`",
                  width: "100%",
                  overflow: "hidden",
                }}
              />
            </a>
          </td>

          {/* Top Right Quadrant: Social Media Icons */}
          <td
            width="200"
            className="bg radius-lg padding-md"
            style={{
              textAlign: "left",
            }}
          >
            {/* Align them in a row */}
            <img
              src={logo}
              alt="Agency Habitat Logo"
              border="0"
              style={{ width: "100%" }}
            />
            <a
              href="tel:{phoneNumber}"
              style={{
                margin: 0,
                fontFamily: "Arial, Helvetica, sans-serif",
                color: "var(--color-primary)",
              }}
            >
              {phoneNumber}
            </a>
            <a
              href="agencyhabitt.com"
              className="text-uppercase text-decoration-none"
            >
              agencyhabitat.com
            </a>
            <a
              href="https://www.facebook.com/Cutera/"
              style={{ marginRight: "5px", textDecoration: "none" }}
            >
              <img
                src="https://stg-cutera23-staging.kinsta.cloud/wp-content/uploads/2023/10/Group-5779.png"
                alt="Facebook"
                width="30px"
                border="0"
              />
            </a>
            <a
              href="https://www.instagram.com/cuterainc"
              style={{ marginRight: "5px", textDecoration: "none" }}
            >
              <img
                src="https://stg-cutera23-staging.kinsta.cloud/wp-content/uploads/2023/11/Instagram-icon-1.png"
                alt="LinkedIn"
                width="30px"
                border="0"
              />
            </a>
            <a
              href="https://twitter.com/Cutera/"
              style={{ marginRight: "5px", textDecoration: "none" }}
            >
              <img
                src="https://stg-cutera23-staging.kinsta.cloud/wp-content/uploads/2023/10/Group-5778.png"
                alt="Twitter/X"
                width="30px"
                border="0"
              />
            </a>
            <a
              href="https://www.linkedin.com/company/cuterainc"
              style={{ textDecoration: "none" }}
            >
              <img
                src="https://stg-cutera23-staging.kinsta.cloud/wp-content/uploads/2023/10/Group-5780.png"
                alt="LinkedIn"
                width="30px"
                border="0"
              />
            </a>
          </td>
        </tr>
        <tr>
          {/* Bottom Left Quadrant: Personal Info */}
          <td
            colspan="2"
            className={`bg-${color.toLowerCase()}`} // Apply the selected color class
            style={{
              padding: "var(--space-sm) var(--space-md)",
              borderRadius: "var(--radius-lg)",
            }}
          >
            <p
              style={{
                margin: 0,
                fontSize: "24px",
                fontFamily: "var(--font-primary)",
                color: "var(--color-primary)",
                fontWeight: "300",
              }}
            >
              {fullName}
            </p>
            <p
              style={{
                margin: 0,
                fontSize: "14px",
                fontFamily: "var(--font-primary)",
                color: "var(--color-primary)",
                textTransform: "uppercase",
              }}
            >
              <em>{jobTitle}</em>
            </p>
          </td>
        </tr>
      </table>
    </div>
  );
}

export default GeneratedSignature;
