import React, { useState, useRef } from "react";

function EmailSignatureForm() {
  const [loading, setLoading] = useState(false);
  const [results, setResults] = useState(null);
  const [formData, setFormData] = useState({
    firstName: "",
    lastName: "",
    jobTitle: "",
    phoneNumber: "",
    color: "",
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value,
    });
  };

  const colorRefs = {
    mint: useRef(),
    coral: useRef(),
    apricot: useRef(),
    mist: useRef(),
    lavender: useRef(),
    tan: useRef(),
  };

  const selectColor = (color) => {
    setFormData({
      ...formData,
      color: color,
    });
  };

  const handleColorClick = (color) => {
    colorRefs[color].current.click();
    selectColor(color);
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    setLoading(true);

    // Simulated results for demonstration
    setTimeout(() => {
      setResults(
        `Full Name: ${formData.firstName} ${formData.lastName}\nJob Title: ${formData.jobTitle}\nPhone Number: ${formData.phoneNumber}\nSelected Color: ${formData.color}`
      );
      setLoading(false);
    }, 2000);
  };

  return (
    <div className="container max-width-xl">
      <div className="grid gap-md items-center justify-center">
        <div className="col">sfsdfsd</div>
        <div className="col">
          <h1 className="font-secondary text-lg margin-top-lg">
            Email Signature Generator
          </h1>

          <div id="emailsigform" className="margin-top-md">
            <form onSubmit={handleSubmit} className="">
              <div>
                {/* ... Other form fields ... */}

                <div>
                  <label className="block">Select your background color:</label>
                  <div className="grid gap-sm">
                    <div className="col">
                      <div
                        className="bg-mint radius-md padding-x-sm padding-y-xs margin-y-sm"
                        onClick={() => handleColorClick("Mint")}
                      >
                        <input
                          type="radio"
                          id="mint"
                          name="color"
                          value="Mint"
                          onChange={handleChange}
                          ref={colorRefs.mint}
                        />
                        <label htmlFor="mint">Mint</label>
                      </div>

                      <div
                        className="bg-coral radius-md padding-x-sm padding-y-xs margin-y-sm"
                        onClick={() => handleColorClick("Coral")}
                      >
                        <input
                          type="radio"
                          id="coral"
                          name="color"
                          value="Coral"
                          onChange={handleChange}
                          ref={colorRefs.coral}
                        />
                        <label htmlFor="coral">Coral</label>
                      </div>

                      <div
                        className="bg-apricot radius-md padding-x-sm padding-y-xs margin-y-sm"
                        onClick={() => handleColorClick("Apricot")}
                      >
                        <input
                          type="radio"
                          id="apricot"
                          name="color"
                          value="Apricot"
                          onChange={handleChange}
                          ref={colorRefs.apricot}
                        />
                        <label htmlFor="apricot">Apricot</label>
                      </div>
                    </div>
                  </div>
                </div>
                <button className="btn btn--primary padding-y-xs" type="submit">
                  Generate Signature
                </button>
              </div>
            </form>

            {loading ? (
              <div>Loading...</div>
            ) : results ? (
              <div>
                <h2>Generated Signature</h2>
                <p>{results}</p>
              </div>
            ) : null}
          </div>
        </div>
      </div>
    </div>
  );
}

export default EmailSignatureForm;
