import React, { useState } from "react";

function EmailSignatureForm() {
  const [loading, setLoading] = useState(false);
  const [results, setResults] = useState(null);
  const [formData, setFormData] = useState({
    firstName: "",
    lastName: "",
    jobTitle: "",
    phoneNumber: "",
    color: "",
  });

  const [showForm, setShowForm] = useState(true);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value,
    });
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    setLoading(true);

    // Simulated results for demonstration
    setTimeout(() => {
      setResults(
        `Full Name: ${formData.firstName} ${formData.lastName}\nJob Title: ${formData.jobTitle}\nPhone Number: ${formData.phoneNumber}\nSelected Color: ${formData.color}`
      );
      setLoading(false);
    }, 2000);

    setShowForm(false);
  };

  const handleGoBack = () => {
    setShowForm(true);
    setResults(null);
  };

  const handleRadioChange = (value) => {
    setFormData({
      ...formData,
      color: value,
    });
  };

  return (
    <div className="container max-width-xl">
      <div className="grid gap-md items-center justify-center">
        <div className="col">sfsdfsd</div>
        <div className="col">
          <h1 className="font-secondary text-lg margin-top-lg">
            Email Signature Generator
          </h1>

          {showForm ? (
            <div id="emailsigform" className="margin-top-md">
              <form onSubmit={handleSubmit} className="">
                <div>
                  <label className="block" htmlFor="first-name">
                    First Name
                  </label>
                  <input
                    type="text"
                    id="first-name"
                    name="firstName"
                    value={formData.firstName}
                    onChange={handleChange}
                    required
                  />
                </div>
                <div>
                  <label className="block" htmlFor="last-name">
                    Last Name
                  </label>
                  <input
                    type="text"
                    id="last-name"
                    name="lastName"
                    value={formData.lastName}
                    onChange={handleChange}
                    required
                  />
                </div>
                <div>
                  <label className="block" htmlFor="job-title">
                    Job Title
                  </label>
                  <input
                    type="text"
                    id="job-title"
                    name="jobTitle"
                    value={formData.jobTitle}
                    onChange={handleChange}
                    required
                  />
                </div>
                <div>
                  <label className="block" htmlFor="phone-number">
                    Your Phone Number (Optional)
                  </label>
                  <input
                    type="tel"
                    id="phone-number"
                    name="phoneNumber"
                    value={formData.phoneNumber}
                    onChange={handleChange}
                    required
                  />
                </div>
                <div>
                  <label className="block">Select your background color:</label>
                  <div className="custom-radio-group grid gap-sm">
                    <div className="col">
                      <label className="custom-radio-container">
                        <input
                          type="radio"
                          name="color"
                          value="Mint"
                          checked={formData.color === "Mint"}
                          onChange={() => handleRadioChange("Mint")}
                        />
                        <div className="custom-radio-button"></div>
                        Mint
                      </label>

                      <label className="custom-radio-container">
                        <input
                          type="radio"
                          name="color"
                          value="Coral"
                          checked={formData.color === "Coral"}
                          onChange={() => handleRadioChange("Coral")}
                        />
                        <div className="custom-radio-button"></div>
                        Coral
                      </label>

                      <label className="custom-radio-container">
                        <input
                          type="radio"
                          name="color"
                          value="Apricot"
                          checked={formData.color === "Apricot"}
                          onChange={() => handleRadioChange("Apricot")}
                        />
                        <div className="custom-radio-button"></div>
                        Apricot
                      </label>
                    </div>

                    <div className="col">
                      <label className="custom-radio-container">
                        <input
                          type="radio"
                          name="color"
                          value="Mist"
                          checked={formData.color === "Mist"}
                          onChange={() => handleRadioChange("Mist")}
                        />
                        <div className="custom-radio-button"></div>
                        Mist
                      </label>

                      <label className="custom-radio-container">
                        <input
                          type="radio"
                          name="color"
                          value="Lavender"
                          checked={formData.color === "Lavender"}
                          onChange={() => handleRadioChange("Lavender")}
                        />
                        <div className="custom-radio-button"></div>
                        Lavender
                      </label>

                      <label className="custom-radio-container">
                        <input
                          type="radio"
                          name="color"
                          value="Tan"
                          checked={formData.color === "Tan"}
                          onChange={() => handleRadioChange("Tan")}
                        />
                        <div className="custom-radio-button"></div>
                        Tan
                      </label>
                    </div>
                  </div>
                </div>
                <button className="btn btn--primary padding-y-xs" type="submit">
                  Generate Signature
                </button>
              </form>
            </div>
          ) : null}

          {loading ? (
            <div>Loading...</div>
          ) : results ? (
            <div>
              <h2>Generated Signature</h2>
              <p>{results}</p>
              <button
                className="btn btn--secondary padding-y-xs"
                onClick={handleGoBack}
              >
                Go Back
              </button>
            </div>
          ) : null}
        </div>
      </div>
    </div>
  );
}

export default EmailSignatureForm;
