import React, { useState } from "react";

function EmailSignatureForm() {
  const [loading, setLoading] = useState(false);
  const [results, setResults] = useState(null);
  const [formData, setFormData] = useState({
    firstName: "",
    lastName: "",
    jobTitle: "",
    phoneNumber: "",
    color: "",
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value,
    });
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    setLoading(true);

    // Simulated results for demonstration
    setTimeout(() => {
      setResults(
        `Full Name: ${formData.firstName} ${formData.lastName}\nJob Title: ${formData.jobTitle}\nPhone Number: ${formData.phoneNumber}\nSelected Color: ${formData.color}`
      );
      setLoading(false);
    }, 2000);
  };

  return (
    <div id="emailsigform" className="margin-top-md">
      <form onSubmit={handleSubmit} className="">
        <div>
          <label className="block" htmlFor="first-name">
            First Name:
          </label>
          <input
            type="text"
            id="first-name"
            name="firstName"
            value={formData.firstName}
            onChange={handleChange}
            required
          />
          <br />
          <br />
        </div>
        <div>
          <label htmlFor="last-name">Last Name:</label>
          <input
            type="text"
            id="last-name"
            name="lastName"
            value={formData.lastName}
            onChange={handleChange}
            required
          />
          <br />
          <br />
        </div>
        <div>
          <label htmlFor="job-title">Job Title:</label>
          <input
            type="text"
            id="job-title"
            name="jobTitle"
            value={formData.jobTitle}
            onChange={handleChange}
            required
          />
          <br />
          <br />
        </div>
        <div>
          <label htmlFor="phone-number">Phone Number:</label>
          <input
            type="tel"
            id="phone-number"
            name="phoneNumber"
            value={formData.phoneNumber}
            onChange={handleChange}
            required
          />
          <br />
          <br />
        </div>
        <div>
          <label>Choose a color:</label>
          <br />
          <input
            type="radio"
            id="mint"
            name="color"
            value="Mint"
            onChange={handleChange}
          />
          <label htmlFor="mint">Mint</label>
          <br />
          <input
            type="radio"
            id="mist"
            name="color"
            value="Mist"
            onChange={handleChange}
          />
          <label htmlFor="mist">Mist</label>
          <br />
          <input
            type="radio"
            id="coral"
            name="color"
            value="Coral"
            onChange={handleChange}
          />
          <label htmlFor="coral">Coral</label>
          <br />
          <input
            type="radio"
            id="lavender"
            name="color"
            value="Lavender"
            onChange={handleChange}
          />
          <label htmlFor="lavender">Lavender</label>
          <br />
          <input
            type="radio"
            id="apricot"
            name="color"
            value="Apricot"
            onChange={handleChange}
          />
          <label htmlFor="apricot">Apricot</label>
          <br />
          <input
            type="radio"
            id="tan"
            name="color"
            value="Tan"
            onChange={handleChange}
          />
          <label htmlFor="tan">Tan</label>
          <br />
          <br />
        </div>
        <button type="submit">Generate Signature</button>
      </form>

      {loading ? (
        <div>Loading...</div>
      ) : results ? (
        <div>
          <h2>Generated Signature</h2>
          <p>{results}</p>
        </div>
      ) : null}
    </div>
  );
}

export default EmailSignatureForm;
