import React, { useState } from "react";

function EmailSignatureForm() {
  const [loading, setLoading] = useState(false);
  const [results, setResults] = useState(null);
  const [formData, setFormData] = useState({
    firstName: "",
    lastName: "",
    jobTitle: "",
    phoneNumber: "",
    color: "",
  });

  const [showForm, setShowForm] = useState(true);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value,
    });
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    setLoading(true);

    // Simulated results for demonstration
    setTimeout(() => {
      setResults(
        `Full Name: ${formData.firstName} ${formData.lastName}\nJob Title: ${formData.jobTitle}\nPhone Number: ${formData.phoneNumber}\nSelected Color: ${formData.color}`
      );
      setLoading(false);
    }, 2000);

    setShowForm(false);
  };

  const handleGoBack = () => {
    setShowForm(true);
    setResults(null);
  };

  return (
    <div className="container max-width-xl">
      <div className="grid gap-md items-center justify-center">
        <div className="col">sfsdfsd</div>
        <div className="col">
          <h1 className="font-secondary text-lg margin-top-lg">
            Email Signature Generator
          </h1>

          {showForm ? (
            <div id="emailsigform" className="margin-top-md">
              <form onSubmit={handleSubmit} className="">
                <div>
                  <label className="block" htmlFor="first-name">
                    First Name
                  </label>
                  <input
                    type="text"
                    id="first-name"
                    name="firstName"
                    value={formData.firstName}
                    onChange={handleChange}
                    required
                  />
                </div>
                {/* Add similar inputs for Last Name, Job Title, Phone Number */}
                <div>
                  <label className="block">Select your background color:</label>
                  <div className="grid gap-sm">
                    <div className="col">
                      <div className="bg-mint radius-md padding-x-sm padding-y-xs margin-y-sm">
                        <input
                          type="radio"
                          id="mint"
                          name="color"
                          value="Mint"
                          onChange={handleChange}
                        />
                        <label htmlFor="mint">Mint</label>
                      </div>

                      {/* Add similar radio buttons for other colors */}
                    </div>
                  </div>
                </div>
                <button className="btn btn--primary padding-y-xs" type="submit">
                  Generate Signature
                </button>
              </form>
            </div>
          ) : null}

          {loading ? (
            <div>Loading...</div>
          ) : results ? (
            <div>
              <h2>Generated Signature</h2>
              <p>{results}</p>
              <button
                className="btn btn--secondary padding-y-xs"
                onClick={handleGoBack}
              >
                Go Back
              </button>
            </div>
          ) : null}
        </div>
      </div>
    </div>
  );
}

export default EmailSignatureForm;
