import React, { useState } from "react";
import GeneratedSignature from "./GeneratedSignature";
import CopyToClipboard from "react-copy-to-clipboard";

function EmailSignatureForm() {
  const [loading, setLoading] = useState(false);
  const [copied, setCopied] = useState(false);
  const [formData, setFormData] = useState({
    firstName: "",
    lastName: "",
    jobTitle: "",
    phoneNumber: "",
    color: "",
  });
  const [generatedSignature, setGeneratedSignature] = useState("");

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value,
    });
  };

  const handleColorClick = (color) => {
    setFormData({
      ...formData,
      color,
    });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    setLoading(true);

    // Simulated results for demonstration
    setTimeout(() => {
      const signature = (
        <GeneratedSignature
          fullName={`${formData.firstName} ${formData.lastName}`}
          jobTitle={formData.jobTitle}
          phoneNumber={formData.phoneNumber}
          emailAddress={formData.emailAddress} // You may need to add emailAddress to your formData state
          address={formData.address} // You may need to add address to your formData state
        />
      );

      const signatureHtml = renderToStaticMarkup(signature);

      setGeneratedSignature(signatureHtml);
      setLoading(false);
    }, 2000);
  };

  const handleCopyHTML = () => {
    setCopied(true);
    copyToClipboard(generatedSignature);
  };

  const handleGoBack = () => {
    setCopied(false);
    setGeneratedSignature("");
    setFormData({
      firstName: "",
      lastName: "",
      jobTitle: "",
      phoneNumber: "",
      color: "",
    });
  };

  return (
    <div className="container max-width-xl">
      <div className="grid gap-md items-center justify-center">
        <div className="col">sfsdfsd</div>
        <div className="col">
          <h1 className="font-secondary text-lg margin-top-lg">
            Email Signature Generator
          </h1>

          <div id="emailsigform" className="margin-top-md">
            {loading ? (
              <div>Our wizards are creating your signature...</div>
            ) : !generatedSignature ? (
              <form onSubmit={handleSubmit} className="">
                <div>
                  <label className="block" htmlFor="first-name">
                    First Name
                  </label>
                  <input
                    type="text"
                    id="first-name"
                    name="firstName"
                    value={formData.firstName}
                    onChange={handleChange}
                    required
                  />
                </div>
                <div>
                  <label className="block" htmlFor="last-name">
                    Last Name
                  </label>
                  <input
                    type="text"
                    id="last-name"
                    name="lastName"
                    value={formData.lastName}
                    onChange={handleChange}
                    required
                  />
                </div>
                <div>
                  <label className="block" htmlFor="job-title">
                    Job Title
                  </label>
                  <input
                    type="text"
                    id="job-title"
                    name="jobTitle"
                    value={formData.jobTitle}
                    onChange={handleChange}
                    required
                  />
                </div>
                <div>
                  <label className="block" htmlFor="phone-number">
                    Your Phone Number (Optional)
                  </label>
                  <input
                    type="tel"
                    id="phone-number"
                    name="phoneNumber"
                    value={formData.phoneNumber}
                    onChange={handleChange}
                  />
                </div>
                <div>
                  <label className="block">Select your background color:</label>
                  <div className="grid gap-sm">
                    <div className="col">
                      <div
                        className="bg-mint radius-md padding-x-sm padding-y-xs margin-y-sm"
                        onClick={() => handleColorClick("Mint")}
                      >
                        <input
                          type="radio"
                          id="mint"
                          name="color"
                          value="Mint"
                          checked={formData.color === "Mint"}
                          onChange={handleChange}
                        />
                        <label htmlFor="mint">Mint</label>
                      </div>

                      <div
                        className="bg-coral radius-md padding-x-sm padding-y-xs margin-y-sm"
                        onClick={() => handleColorClick("Coral")}
                      >
                        <input
                          type="radio"
                          id="coral"
                          name="color"
                          value="Coral"
                          checked={formData.color === "Coral"}
                          onChange={handleChange}
                        />
                        <label htmlFor="coral">Coral</label>
                      </div>

                      <div
                        className="bg-apricot radius-md padding-x-sm padding-y-xs margin-y-sm"
                        onClick={() => handleColorClick("Apricot")}
                      >
                        <input
                          type="radio"
                          id="apricot"
                          name="color"
                          value="Apricot"
                          checked={formData.color === "Apricot"}
                          onChange={handleChange}
                        />
                        <label htmlFor="apricot">Apricot</label>
                      </div>
                    </div>

                    <div className="col">
                      <div
                        className="bg-mist radius-md padding-x-sm padding-y-xs margin-y-sm"
                        onClick={() => handleColorClick("Mist")}
                      >
                        <input
                          type="radio"
                          id="mist"
                          name="color"
                          value="Mist"
                          checked={formData.color === "Mist"}
                          onChange={handleChange}
                        />
                        <label htmlFor="mist">Mist</label>
                      </div>

                      <div
                        className="bg-lavendar radius-md padding-x-sm padding-y-xs margin-y-sm"
                        onClick={() => handleColorClick("Lavender")}
                      >
                        <input
                          type="radio"
                          id="lavender"
                          name="color"
                          value="Lavender"
                          checked={formData.color === "Lavender"}
                          onChange={handleChange}
                        />
                        <label htmlFor="lavendar">Lavender</label>
                      </div>

                      <div
                        className="bg-accent radius-md padding-x-sm padding-y-xs margin-y-sm"
                        onClick={() => handleColorClick("Tan")}
                      >
                        <input
                          type="radio"
                          id="tan"
                          name="color"
                          value="Tan"
                          checked={formData.color === "Tan"}
                          onChange={handleChange}
                        />
                        <label htmlFor="tan">Tan</label>
                      </div>
                    </div>
                  </div>
                </div>
                <button className="btn btn--primary padding-y-xs" type="submit">
                  Generate Signature
                </button>
              </form>
            ) : (
              <div>
                <div
                  dangerouslySetInnerHTML={{ __html: generatedSignature }}
                ></div>

                <CopyToClipboard
                  text={generatedSignature}
                  onCopy={handleCopyHTML}
                >
                  <button className="margin-top-md btn btn--primary padding-y-xs">
                    {copied ? "HTML Copied!" : "Copy HTML"}
                  </button>
                </CopyToClipboard>

                <button
                  className="margin-top-md btn btn--primary padding-y-xs"
                  onClick={handleGoBack}
                >
                  Back
                </button>
              </div>
            )}
          </div>
        </div>
      </div>
    </div>
  );
}

export default EmailSignatureForm;
