import React, { useState } from "react";
import GeneratedSignature from "./GeneratedSignature";
import CopyToClipboard from "react-copy-to-clipboard";

function EmailSignatureForm() {
  const [loading, setLoading] = useState(false);
  const [results, setResults] = useState(null);
  const [copied, setCopied] = useState(false);
  const [formData, setFormData] = useState({
    firstName: "",
    lastName: "",
    jobTitle: "",
    phoneNumber: "",
    color: "",
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value,
    });
  };

  const handleColorClick = (color) => {
    setFormData({
      ...formData,
      color,
    });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    setLoading(true);

    // Simulated results for demonstration
    setTimeout(() => {
      setResults(
        <GeneratedSignature
          fullName={`${formData.firstName} ${formData.lastName}`}
          jobTitle={formData.jobTitle}
          phoneNumber={formData.phoneNumber}
          emailAddress={formData.emailAddress}
          address={formData.address}
          color={formData.color} // Pass the color prop
        />
      );

      setLoading(false);
    }, 2000);
  };

  const handleGoBack = () => {
    setResults(null);
    setCopied(false);
    setFormData({
      firstName: "",
      lastName: "",
      jobTitle: "",
      phoneNumber: "",
      color: "",
    });
  };

  return (
    <div className="container max-width-xl">
      <div className="grid gap-md justify-center">
        {loading ? (
          <div>Our wizards are creating your signature...</div>
        ) : results ? (
          <>
            <div className="grid gap-md justify-center margin-y-0">
              <div class="col-12 margin-0">
                <h1 className="margin-0 text-lg font-secondary">
                  Email Signature Generator
                </h1>
              </div>
            </div>
            <div className="grid gap-md justify-center margin-top-0">
              <div class="col">
                <p>{results}</p>
              </div>
              <div class="col">
                <div
                  className="padding-lg radius-lg"
                  style={{ background: "#E9E3DD", color: "#070F0B" }}
                >
                  <h2 className="text-md font-secondary margin-bottom-xs">
                    Instructions:
                  </h2>
                  <ul class="font-primary">
                    <li>Click Copy to Clipboard</li>
                    <li>Outlook > Preferences > Signatures > Edit</li>
                    <li>
                      Delete all the content and paste your email signature >
                      Save
                    </li>
                    <li>
                      You can rename the “Standard” email signature to Habitat
                    </li>
                    <li>
                      Under “Choose Default Signature” Select your email
                      signature for “New Messages” and “Replies/Forward”
                    </li>
                    <li>You're done!</li>
                  </ul>
                </div>
                <div className="text-right">
                  <CopyToClipboard
                    text={results}
                    onCopy={() => setCopied(true)}
                  >
                    <button className="margin-top-md btn btn--primary padding-y-xs margin-right-sm">
                      {copied ? "HTML Copied!" : "Copy to Clipboard"}
                    </button>
                  </CopyToClipboard>

                  <button
                    className="margin-top-md btn btn--primary padding-y-xs"
                    onClick={handleGoBack}
                  >
                    Back
                  </button>
                </div>
              </div>
            </div>
          </>
        ) : (
          <>
            <div className="col">sfsdfsd</div>
            <div className="col">
              <h1 className="font-secondary text-lg margin-top-lg">
                Email Signature Generator
              </h1>

              <div id="emailsigform" className="margin-top-md">
                <form onSubmit={handleSubmit} className="">
                  <div>
                    <label className="block" htmlFor="first-name">
                      First Name
                    </label>
                    <input
                      type="text"
                      id="first-name"
                      name="firstName"
                      value={formData.firstName}
                      onChange={handleChange}
                      required
                    />
                  </div>
                  <div>
                    <label className="block" htmlFor="last-name">
                      Last Name
                    </label>
                    <input
                      type="text"
                      id="last-name"
                      name="lastName"
                      value={formData.lastName}
                      onChange={handleChange}
                      required
                    />
                  </div>
                  <div>
                    <label className="block" htmlFor="job-title">
                      Job Title
                    </label>
                    <input
                      type="text"
                      id="job-title"
                      name="jobTitle"
                      value={formData.jobTitle}
                      onChange={handleChange}
                      required
                    />
                  </div>
                  <div>
                    <label className="block" htmlFor="phone-number">
                      Your Phone Number (Optional)
                    </label>
                    <input
                      type="tel"
                      id="phone-number"
                      name="phoneNumber"
                      value={formData.phoneNumber}
                      onChange={handleChange}
                    />
                  </div>
                  <div>
                    <label className="block">
                      Select your background color:
                    </label>
                    <div className="grid gap-sm">
                      <div className="col">
                        <div
                          className={`bg-mint radius-md padding-x-sm padding-y-xs margin-y-sm ${
                            formData.color === "Mint" ? "selected" : ""
                          }`}
                          onClick={() => handleColorClick("Mint")}
                        >
                          <input
                            type="radio"
                            id="mint"
                            name="color"
                            value="Mint"
                            checked={formData.color === "Mint"}
                            onChange={handleChange}
                          />
